import useCounter from "./hook/useCounter";

function App() {
  const { count, makeIncrement, makeDecrement } = useCounter();

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="bg-white shadow-lg rounded-2xl p-8 text-center w-80">
        <h1 className="text-xl font-bold text-gray-800 mb-6">
          This counter demonstrates abstraction using a custom React hook.
        </h1>

        <div className="flex items-center justify-center gap-6 mb-6">
          <button
            onClick={makeDecrement}

          >
            -
          </button>

          <span >{count}</span>

          <button
            onClick={makeIncrement}

          >
            +
          </button>
        </div>

        <p className="text-sm text-gray-500">

        </p>
      </div>
    </div>
  );
}

export default App;
